import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import MovieList from './components/MovieList';
import MovieListHeading from './components/MovieListHeading';
import SearchBox from './components/SearchBox';
import AddFavourite from './components/AddToFavorites';

const App = () => {
	const [movies, setMovies] = useState([]);
  const [searchValue, setSearchValue] = useState('');
  const [favourites,setFavourites] = useState([])

  const getMovieRequest = async (searchValue) => {

    // let url = "http://www.omdbapi.com/?s=starwars&apikey=652f4f1"

    const url = `http://www.omdbapi.com/?s=${searchValue}&apikey=652f4f1`;

		const response = await fetch(url);
		const responseJson = await response.json();

		if (responseJson.Search) {
			setMovies(responseJson.Search);
		}
  }

  const addFavouriteMovie = () => {
    const newFavouriteList = [...favourites, movies];
		setFavourites(newFavouriteList);
  }

  useEffect (()  => {
    getMovieRequest(searchValue)
    console.log("movies",movies)
  },[searchValue])

	return (
		<div className='container-fluid movie-app'>
      <div className='row d-flex align-items-center mt-4 mb-4'>
				<MovieListHeading heading='Movies' />

        <SearchBox searchValue={searchValue} setSearchValue={setSearchValue} />
      </div>
			<div className='row'>
        <MovieList movies={movies} favourite={AddFavourite} handleFavouritesClick={addFavouriteMovie}/>
			</div>
		</div>
	);
};

export default App;
